    {
        parent::__construct();
    }
    
    /*
     * Get instagram by id
     */
    function get_instagram($id)
    {
        return $this->db->get_where('instagram',array('id'=>$id))->row_array();
    }
        
    /*
     * Get all instagram
     */
    function get_all_instagram()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('instagram')->result_array();
    }
        
