     */
    function update_instagram($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('instagram',$params);
    }
    
    /*
     * function to delete instagram
     */
    function delete_instagram($id)
    {
        return $this->db->delete('instagram',array('id'=>$id));
    }
